import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { Button, Dialog, DialogTitle, DialogContent, DialogActions } from "@mui/material";
import moment from 'moment';
import AdNavBar from "../../components/AdNavBar";
import "./AdHome.css";

const AdHome = () => {
  const [newPostContent, setNewPostContent] = useState("");
  const [posts, setPosts] = useState([]);
  const [comments, setComments] = useState([]);
  const [loggedInAdmin, setLoggedInAdmin] = useState(null);
  const [profilePicture, setProfilePicture] = useState(null);
  const [adminProfilePictures, setAdminProfilePictures] = useState({});
  const defaultProfile = '/dp.png';
  const [imagePreview, setImagePreview] = useState(null);
  const [isCommentDialogOpen, setIsCommentDialogOpen] = useState(false);
  const [currentPostId, setCurrentPostId] = useState(null);
  const [isDeletePostDialogOpen, setIsDeletePostDialogOpen] = useState(false);
  const [isDeleteCommentDialogOpen, setIsDeleteCommentDialogOpen] = useState(false);
  const [itemToDelete, setItemToDelete] = useState(null);

  const fileInputRef = useRef(null);

  // Fetch posts and admin profile pictures
  useEffect(() => {
    const fetchPostsAndPictures = async () => {
      try {
        const response = await axios.get("http://localhost:8080/posts");
        setPosts(response.data);
        const adminIds = new Set(response.data.map(post => post.adminId));
        adminIds.forEach(adminId => fetchAdminProfilePicture(adminId));
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };
    fetchPostsAndPictures();
  }, []);

  const fetchAdminProfilePicture = async (adminId) => {
    try {
      const response = await fetch(`http://localhost:8080/admin/profile/getProfilePicture/${adminId}`);
      if (response.ok) {
        const imageBlob = await response.blob();
        const imageUrl = URL.createObjectURL(imageBlob);
        setAdminProfilePictures(prev => ({ ...prev, [adminId]: imageUrl }));
      } else {
        setAdminProfilePictures(prev => ({ ...prev, [adminId]: defaultProfile }));
      }
    } catch (error) {
      console.error('Failed to fetch admin profile picture:', error);
    }
  };

  // Handle new post submission
  const handlePostSubmit = async (e) => {
    e.preventDefault();
    if (!newPostContent && !imagePreview) {
      alert("Please enter a post or select a picture before submitting.");
      return;
    }

    const newPost = {
      content: newPostContent,
      image: imagePreview,
      adminId: loggedInAdmin.adminId,
      fullName: loggedInAdmin.fullName,
      idNumber: loggedInAdmin.idNumber,
      isVerified: false,
      likes: 0,
      dislikes: 0,
    };

    try {
      const response = await axios.post("http://localhost:8080/posts/add", newPost, {
        headers: { 'Content-Type': 'application/json' }
      });
      setPosts(prevPosts => [response.data, ...prevPosts]);
      setNewPostContent("");
      setImagePreview(null);
    } catch (error) {
      console.error("Error posting data:", error);
    }
  };

  // Handle post like/dislike
  const handleLike = async (postId) => {
    try {
      const response = await axios.post(`http://localhost:8080/posts/${postId}/like`, {}, {
        params: { userId: loggedInAdmin.adminId }
      });
      setPosts(posts.map(post => post.postId === postId ? response.data : post));
    } catch (error) {
      console.error("Error liking post:", error);
    }
  };

  const handleDislike = async (postId) => {
    try {
      const response = await axios.post(`http://localhost:8080/posts/${postId}/dislike`, {}, {
        params: { userId: loggedInAdmin.adminId }
      });
      setPosts(posts.map(post => post.postId === postId ? response.data : post));
    } catch (error) {
      console.error("Error disliking post:", error);
    }
  };

  // Handle comment-related functions
  const handleOpenComments = async (postId) => {
    setCurrentPostId(postId);
    try {
      const response = await axios.get(`http://localhost:8080/comments/${postId}`);
      setComments(response.data);
      setIsCommentDialogOpen(true);
    } catch (error) {
      console.error("Error fetching comments:", error);
    }
  };

  const handleDeleteComment = (commentId) => {
    setItemToDelete(commentId);
    setIsDeleteCommentDialogOpen(true);
  };

  const confirmDeleteComment = async () => {
    try {
      await axios.delete(`http://localhost:8080/comments/${itemToDelete}`, {
        params: { adminId: loggedInAdmin.adminId }
      });
      setComments(comments.filter(comment => comment.commentId !== itemToDelete));
      setIsDeleteCommentDialogOpen(false);
    } catch (error) {
      console.error("Error deleting comment:", error);
    }
  };

  // Delete post functionality
  const handleDeletePost = (postId) => {
    setItemToDelete(postId);
    setIsDeletePostDialogOpen(true);
  };

  const confirmDeletePost = async () => {
    try {
      await axios.delete(`http://localhost:8080/posts/${itemToDelete}`);
      setPosts(posts.filter(post => post.postId !== itemToDelete));
      setIsDeletePostDialogOpen(false);
    } catch (error) {
      console.error("Error deleting post:", error);
    }
  };

  const formatTimestamp = (timestamp) => moment(timestamp).format('dddd, MMMM D, YYYY [at] h:mm A');
  const getRelativeTime = (timestamp) => moment(timestamp).fromNow();

  return (
    <div className="adhome">
      <AdNavBar />
      <b className="HWildcat">WILDCAT</b>

      <div className="content-wrapper">
        <div className="post-container">
          <div className="logo-container">
            <img src={profilePicture || defaultProfile} alt="Admin Avatar" className="admins-dp" />
          </div>
          <div className="post-form">
            <form onSubmit={handlePostSubmit}>
              <div className="input-container">
                <input
                  type="text"
                  className="post-input"
                  value={newPostContent}
                  onChange={(e) => setNewPostContent(e.target.value)}
                  placeholder="What's happening in your day, Wildcat?"
                />
              </div>
              <Button type="submit" className="post-button" variant="contained">POST</Button>
            </form>
            {imagePreview && (
              <div className="image-preview">
                <img src={imagePreview} alt="Preview" style={{ width: '100px', height: '100px' }} />
              </div>
            )}
          </div>
        </div>

        <div className="post-list">
          {posts.map(post => (
            <div key={post.postId} className="post-card">
              <div className="card-container">
                <div className="name-container">
                  <img src={adminProfilePictures[post.adminId] || defaultProfile} alt="Admin Avatar" />
                  <h5>{post.fullName} ({post.idNumber})</h5>
                  {loggedInAdmin?.adminId === post.adminId && (
                    <img
                      src="/delete.png"
                      alt="Delete"
                      onClick={() => handleDeletePost(post.postId)}
                      style={{ cursor: 'pointer', width: '20px', height: '20px' }}
                    />
                  )}
                </div>
                <div className="timestamp">
                  <span className="formatted-date">{formatTimestamp(post.timestamp)}</span>
                  <br />
                  <span className="relative-time">{getRelativeTime(post.timestamp)}</span>
                </div>
                <div className="card-contents">
                  <p>{post.content}</p>
                  {post.image && <img src={post.image} alt="Post" className="post-image" />}
                </div>
                <div className="footer-actions">
                  <button onClick={() => handleLike(post.postId)}>
                    <img src="/t-up.png" alt="Like" /> {post.likes}
                  </button>
                  <button onClick={() => handleDislike(post.postId)}>
                    <img src="/t-down.png" alt="Dislike" /> {post.dislikes}
                  </button>
                  <button onClick={() => handleOpenComments(post.postId)}>Comment</button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Comment Dialog */}
      <Dialog open={isCommentDialogOpen} onClose={() => setIsCommentDialogOpen(false)}>
        <DialogTitle>Comments</DialogTitle>
        <DialogContent>
          {comments.map(comment => (
            <div key={comment.commentId} className="comment">
              <span>{comment.fullName} ({comment.idNumber})</span>
              <p>{comment.content}</p>
              {loggedInAdmin?.adminId === comment.adminId && (
                <img
                  src="/delete.png"
                  alt="Delete Comment"
                  onClick={() => handleDeleteComment(comment.commentId)}
                />
              )}
            </div>
          ))}
        </DialogContent>
      </Dialog>

      {/* Confirm Delete Post */}
      <Dialog open={isDeletePostDialogOpen} onClose={() => setIsDeletePostDialogOpen(false)}>
        <DialogTitle>Confirm Delete</DialogTitle>
        <DialogContent>Are you sure you want to delete this post?</DialogContent>
        <DialogActions>
          <Button onClick={() => setIsDeletePostDialogOpen(false)}>Cancel</Button>
          <Button onClick={confirmDeletePost}>Delete</Button>
        </DialogActions>
      </Dialog>

      {/* Confirm Delete Comment */}
      <Dialog open={isDeleteCommentDialogOpen} onClose={() => setIsDeleteCommentDialogOpen(false)}>
        <DialogTitle>Confirm Delete</DialogTitle>
        <DialogContent>Are you sure you want to delete this comment?</DialogContent>
        <DialogActions>
          <Button onClick={() => setIsDeleteCommentDialogOpen(false)}>Cancel</Button>
          <Button onClick={confirmDeleteComment}>Delete</Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default AdHome;
